mapper.select:
SELECT tb.id , tb.title , tb.author , COALESCE(tb.publisher, '') AS publisher , COALESCE(tb.publish_dt, '') AS publish_dt , COALESCE(tb.book_details, '') AS book_details , COALESCE(tb.isbn, '') AS isbn , tfm.file_seq AS thumb_seq , COALESCE(tbl.books_amount, 0) AS books_amount , COALESCE(tbl.sell_amount, 0) AS sell_amount FROM hun_test.tb_books tb LEFT JOIN ( SELECT id , SUM( CASE WHEN type = 'O' OR type = 'S' THEN books_amount -1 ELSE books_amount END ) AS books_amount , SUM( CASE WHEN type = 'S' THEN books_amount WHEN type = 'R' THEN books_amount -1 ELSE 0 END ) AS sell_amount FROM hun_test.tb_books_log tbl GROUP BY id ) tbl ON tb.id = tbl.id LEFT JOIN hun_test.tb_file_mng tfm ON tb.id = tfm.rel_pk::NUMERIC AND tfm.rel_table = 'tb_books' AND tfm.rel_column = 'thumbnail' AND tfm.del_yn <> 'Y' WHERE tb.del_yn <> 'Y'
AND ( tb.title LIKE CONCAT('%', #{searchKeyword}, '%') OR tb.author LIKE CONCAT('%', #{searchKeyword}, '%') OR tb.publisher LIKE CONCAT('%', #{searchKeyword}, '%') OR tb.bookDetails LIKE CONCAT('%', #{searchKeyword}, '%') )
ORDER BY tb.id DESC OFFSET #{startRow} LIMIT #{viewCnt}

mapper.insert:
INSERT INTO hun_test.tb_books (
	title
	, author
	, publisher
	, publish_dt
	, book_details
	, isbn
	, crt_dt
) values (
	#{title}
	, #{author}
	, #{publisher}
	, #{publishDt}
	, #{bookDetails}
	, #{isbn}
	, CURRENT_TIMESTAMP
)

mapper.update:
UPDATE
	hun_test.tb_books
SET
	title = #{title}
	, author = #{author}
	, publisher = #{publisher}
	, publish_dt = #{publishDt}
	, book_details = #{bookDetails}
	, isbn = #{isbn}
	, udt_dt = CURRENT_TIMESTAMP
WHERE
	id = #{id}

mapper.insert:
INSERT INTO hun_test.tb_books_log (
	id
	, type
	, books_amount
	, log_dt
) VALUES (
	#{id}
	, #{type}
	, COALESCE(#{booksAmount}, 0)
	, CURRENT_TIMESTAMP
)
